mapper.select:
SELECT r.id, r.member_id, r.hotel_id, r.reservations_id, r.rating, r.review_content, r.review_card, r.created_at, r.updated_at, m.provider, m.first_name, m.last_name, m.email, mi.member_image_path as profileImage FROM reviews_tbl r LEFT JOIN member_tbl m ON r.member_id = m.id LEFT JOIN member_image_tbl mi ON m.id = mi.member_id AND mi.image_type = 'PROFILE' WHERE r.hotel_id = #{hotelId}
AND r.review_card = #{reviewCard}
ORDER BY r.rating DESC, r.created_at DESC
ORDER BY r.rating ASC, r.created_at DESC
ORDER BY r.created_at DESC

mapper.delete:
DELETE FROM reviews_tbl
WHERE id = #{id}
  AND member_id = #{memberId}
