mapper.select:
SELECT rp.id, rp.date, rp.price, rp.created_at as createdAt, rp.updated_at as updatedAt, rp.room_id as roomId, r.room_name as roomName, h.hotel_name as hotelName FROM room_pricing_tbl rp INNER JOIN room_tbl r ON rp.room_id = r.id INNER JOIN hotel_tbl h ON r.hotel_id = h.id
r.room_name LIKE CONCAT('%', #{RoomName}, '%') OR h.hotel_name LIKE CONCAT('%', #{RoomName}, '%')
ORDER BY rp.id DESC LIMIT #{size} OFFSET #{offset}
